import { EmptyState, Layout, Page, Select, Button } from '@shopify/polaris';
import Cookies from 'js-cookie';
const img = 'https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg';



class Index extends React.Component {
  constructor(props){
    super(props);
    this.state = {
      selected: '',
      themes: [],
      redirect: false
    };
  }

  componentDidMount(){
    this.getThemes();
  }

  render() {
    return (
      <Page>
        <Layout>
          <EmptyState
            action={{
              content: 'Add',
              onAction: () => this.assetUpdateRequest()
            }}
          >
            <Select
              options = {this.state.themes ? this.state.themes.map(el => {return{label: `${el.name}`, value:`${el.id}`}}) : null}
              onChange={this.handleChange}
              value={this.state.selected}
              placeholder = "select a theme"
               />
               {this.renderRedirect()}
          </EmptyState>
        </Layout>
      </Page>
    );
  };


  getThemes = async () => {
    fetch("/themes", { method: "GET"})
    .then(response => response.json())
    .then(json => this.setState({themes: json.data.themes}))
  };

  handleChange = (newValue) => {
    this.setState({selected: newValue});
  };

  renderRedirect = () => {
    if (this.state.redirect) {
      return (
        <a
          target="_blank"
          href={'http://' + Cookies.get('shopOrigin') + `/admin/themes/${this.state.selected}/editor`}>
            <Button primary>GO TO CUSTOMIZER</Button>
        </a>)
    }
  }

  assetUpdateRequest = async () => {
    var fetchUrl = "/api/" + this.state.selected; 
    var method = "PUT";
    fetch(fetchUrl, { method: method })
    .then(response => response.json())
    .then(json => {
      if (json.status === 'success'){
        this.setState({redirect: true})
      }
    })
  }
}

export default Index;
